package TypeCast;

public class ImplicitTypecasting {

	private static final String Syetem = null;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
//		int to double;
		int a=5;
		double d= a;
		System.out.println(d);
		
		char c ='A';
		int b= c;
		System.out.println(b);
		
//		byte -float
		
		byte e= 15;
		float f= e;
		System.out.println("changed = " +f);
		
//		char -double
		
		char cc='a';
		double dd = cc;
		System.out.println("changed = " +dd);
		
//		int - float
		
		int aa= 151;
		float ff=aa;
		System.out.println("changed = " +ff);
		
//		long - double
		
		long l=20000;
		double x =l;
		System.out.println("changed = " +l);
		
		

	}

}
